CREATE OR ALTER PROCEDURE P_STOCKREGULATE (
    I_BARCODE TYPE OF COLUMN VAREFRVSTR.V509INDEX,
    I_DEPARTMENT TYPE OF COLUMN AFDELING.AFDELINGSNUMMER,
    I_MACHINE TYPE OF COLUMN UNDERAFDELING.NAVN,
    I_CLERK TYPE OF COLUMN EKSPEDIENTER.NR,
    I_DATETIME DATE,
    I_RECEIPTNUMBER INTEGER,
    I_QUANTITY NUMERIC(15,2),
    I_COSTPRICE NUMERIC(15,2),
    I_SALESPRICE NUMERIC(15,2),
    I_TEXT TYPE OF COLUMN TRANSAKTIONER.BONTEXT,
    I_KINDOF VARCHAR(20))
RETURNS (
    O_RECEIPTNUMBER INTEGER)
AS
DECLARE VARIABLE LRECEIPTNUMBER INTEGER;
DECLARE VARIABLE LSEPKVITNR INTEGER;
DECLARE VARIABLE LGENERATORNAME TYPE OF COLUMN SEP_KVITNR.GEN_NAVN;
DECLARE VARIABLE LVAREPLU_ID TYPE OF COLUMN VAREFRVSTR.VAREPLU_ID;
DECLARE VARIABLE LFARVE_NAVN TYPE OF COLUMN VAREFRVSTR.FARVE_NAVN;
DECLARE VARIABLE LLAENGDE_NAVN TYPE OF COLUMN VAREFRVSTR.LAENGDE_NAVN;
DECLARE VARIABLE LSTOERRELSE_NAVN TYPE OF COLUMN VAREFRVSTR.STOERRELSE_NAVN;
DECLARE VARIABLE LV509INDEX TYPE OF COLUMN VAREFRVSTR.V509INDEX;
DECLARE VARIABLE LLEVERID TYPE OF COLUMN VAREFRVSTR.LEVERID;
DECLARE VARIABLE LVAREGRPID TYPE OF COLUMN VAREFRVSTR.VAREGRPID; /* DECLARE VARIABLE momssats, */
DECLARE VARIABLE LMOMSSATS TYPE OF COLUMN MOMSSATSER.MOMSSATS;
DECLARE VARIABLE LVEJETKOSTPRISSTK TYPE OF COLUMN VAREFRVSTR_DETAIL.VEJETKOSTPRISSTK;
DECLARE VARIABLE LSALGSPRISSTK TYPE OF COLUMN VAREFRVSTR_DETAIL.SALGSPRISSTK;
DECLARE VARIABLE LVALUTA_NAVN TYPE OF COLUMN VAREFRVSTR_DETAIL.VALUTA_NAVN;
DECLARE VARIABLE LANTALSTK TYPE OF COLUMN VAREFRVSTR_DETAIL.ANTALSTK;
BEGIN
  /*
    Procedure created: 03-07-2024
    Author: Mivi
    Purpose: To regulate a variant in a department
    Input (which must be cleared by caller):
     - Barcodeor EANNumber.
     - Department in which the regulation must be made
     - Machine (can be empty)
     - Clerk that made the regulation
     - Date and time of the regulation
     - Receipt number. Can be empty and then we need to fetch the next receipt number for this machine and/or department.
     - Quantity. Must not be 0
     - Costprice. If 0 - use what is on item
     - salesprice. If 0 - use what is on item
     - Kindof - If "regulate" regulate item with quantity. If "set to" find current stock, and see what needs to be regulated to end on Quantity

    When it all has been done, we return the receipt number

    Consider:
     - What to do about "SidsteKbbStk" on VARER_DETAIL

    We need to do:

       - Calculate VAT part
       - Regulate variant (record in VAREFRVSTR_DETAIL
       - Update monthly records to item and variant
     - IGNORED: Update Color, Size and Length
     - IGNORED: Update clerk
     - Update Supplier and Group
     - Insert transaction
  */

  /*Now - Lets fetch variants supplier, group, color, size, length, VAT etc*/
  SELECT
      V.VAREPLU_ID,
      V.FARVE_NAVN,
      V.LAENGDE_NAVN,
      V.STOERRELSE_NAVN,
      V.V509INDEX,
      V.LEVERID,
      V.VAREGRPID,
      M.MOMSSATS,
      VD.VEJETKOSTPRISSTK,
      VD.SALGSPRISSTK,
      VD.VALUTA_NAVN,
      VD.ANTALSTK
  FROM VAREFRVSTR V
      INNER JOIN VAREFRVSTR_DETAIL VD ON
            V.V509INDEX = VD.V509INDEX
            AND VD.AFDELING_ID = :I_DEPARTMENT
      INNER JOIN MOMSSATSER M ON
            M.MOMSSATS_TITEL = VD.MOMSSATS
  WHERE
      (V.V509INDEX = :I_BARCODE)
      OR (V.EANNUMMER = :I_BARCODE)
  INTO :LVAREPLU_ID,
       :LFARVE_NAVN,
       :LLAENGDE_NAVN,
       :LSTOERRELSE_NAVN,
       :LV509INDEX,
       :LLEVERID,
       :LVAREGRPID,
       :LMOMSSATS,
       :LVEJETKOSTPRISSTK,
       :LSALGSPRISSTK,
       :LVALUTA_NAVN,
       :LANTALSTK;
  IF (:LV509INDEX IS NULL) THEN
  BEGIN
    /*item does not exists - skip*/
    :o_receiptnumber = :i_receiptnumber;
  END
  ELSE
  BEGIN
    /*Lets check receipt number. If 0 fetch one*/
    IF (:I_RECEIPTNUMBER = 0) THEN
    BEGIN
      /*LLets see if the system is set to use separate receipts numbers in each department / machine*/
      SELECT
          GLOBALSTAMDATA.SEP_KVITNR
      FROM GLOBALSTAMDATA
      WHERE
          POSTNR = 2
      INTO :LSEPKVITNR;

      IF (:LSEPKVITNR = 0) THEN
      BEGIN
        /*Each the same for all*/
        :LGENERATORNAME = 'GETNEXTKVITNR';
      END
      ELSE
      BEGIN
        /*use separate - Can currently only be on department level. Not on machine level*/
        :LGENERATORNAME = 'NEXTKVITNR' || :I_DEPARTMENT;
      END
      /*Lets find the next reference number (receipt number) for this stock regulation*/
      EXECUTE STATEMENT 'select gen_id(' || LGENERATORNAME || ',1) from rdb$database'
          INTO :LRECEIPTNUMBER;
    END
    ELSE
    BEGIN
      :LRECEIPTNUMBER = :I_RECEIPTNUMBER;
    END

    IF (:I_KINDOF = 'set to') THEN
    BEGIN
      /*We have to set stock to :i_Quantity. Calculate what we need to regulate with*/
      IF (:LANTALSTK <> :I_QUANTITY) THEN
      BEGIN
        :I_QUANTITY = (:I_QUANTITY - :LANTALSTK);
      END
      ELSE
      BEGIN
        :I_QUANTITY = 0;
      END
    END

    IF (:I_QUANTITY <> 0) THEN
    BEGIN
      /*If cost- or salesprices are set as parameters then use them*/
      IF (:I_COSTPRICE <> 0) THEN
        :LVEJETKOSTPRISSTK = :I_COSTPRICE;
      IF (:I_SALESPRICE <> 0) THEN
        :LSALGSPRISSTK = :I_SALESPRICE;

      /*Now - lets regulate the variant.*/
      UPDATE VAREFRVSTR_DETAIL SET
          ANTALSTK = ANTALSTK + :I_QUANTITY,
          BEH_KOSTPRIS = BEH_KOSTPRIS + (:I_QUANTITY * :LVEJETKOSTPRISSTK),
          BEH_SALGSPRIS = BEH_SALGSPRIS + (:I_QUANTITY * :LSALGSPRISSTK),
          BEH_SALGSPRISMOMS = BEH_SALGSPRISMOMS + (:I_QUANTITY * :LSALGSPRISSTK) - ((:I_QUANTITY * :LSALGSPRISSTK) / (1 + (:LMOMSSATS / 100))),
          SALGSPRISSTK = :LSALGSPRISSTK,
          SIDSTEKOBSSTK = :I_QUANTITY,
          SIDSTEKOSTPRSTK = (:I_QUANTITY * :LVEJETKOSTPRISSTK),   /*This field is the TOTAL costprice for the above quantity. Its important when ANTALSTK ends on 0 (due to triggers)*/
          SIDSTETILGANG = 'NOW',
          SIDSTEBEV = 'NOW'
      WHERE
          V509INDEX = :I_BARCODE
          AND AFDELING_ID = :I_DEPARTMENT;


      /*Lets update monthly records to variants*/
      EXECUTE PROCEDURE OPDAT_VVOMSETKOEB(:LV509INDEX,
                                          :I_DEPARTMENT,
                                          EXTRACT(MONTH FROM :I_DATETIME),
                                          EXTRACT(YEAR FROM :I_DATETIME),
                                          :I_QUANTITY,
                                          (:I_QUANTITY * :LSALGSPRISSTK),
                                          (:I_QUANTITY * :LVEJETKOSTPRISSTK),
                                          (:I_QUANTITY * :LSALGSPRISSTK) - ((:I_QUANTITY * :LSALGSPRISSTK) / (1 + (:LMOMSSATS / 100))));

      /*Lets update monthly records to head item*/
      EXECUTE PROCEDURE OPDAT_VAREROMSETKOEB(:LVAREPLU_ID,
                                             :I_DEPARTMENT,
                                             EXTRACT(MONTH FROM :I_DATETIME),
                                             EXTRACT(YEAR FROM :I_DATETIME),
                                             :I_QUANTITY,
                                             (:I_QUANTITY * :LSALGSPRISSTK),
                                             (:I_QUANTITY * :LVEJETKOSTPRISSTK),
                                             (:I_QUANTITY * :LSALGSPRISSTK) - ((:I_QUANTITY * :LSALGSPRISSTK) / (1 + (:LMOMSSATS / 100))));

      /*Lets update monthly records to supplier*/
      EXECUTE PROCEDURE OPDAT_LEVERANDOROMSETKOEB(:LLEVERID,
                                                  :I_DEPARTMENT,
                                                  EXTRACT(MONTH FROM :I_DATETIME),
                                                  EXTRACT(YEAR FROM :I_DATETIME),
                                                  :I_QUANTITY,
                                                  (:I_QUANTITY * :LSALGSPRISSTK),
                                                  (:I_QUANTITY * :LVEJETKOSTPRISSTK),
                                                  (:I_QUANTITY * :LSALGSPRISSTK) - ((:I_QUANTITY * :LSALGSPRISSTK) / (1 + (:LMOMSSATS / 100))));

      /*Lets update monthly records to group*/
      EXECUTE PROCEDURE OPDAT_VAREGRUPPEROMSETKOEB(:LVAREGRPID,
                                                   :I_DEPARTMENT,
                                                   EXTRACT(MONTH FROM :I_DATETIME),
                                                   EXTRACT(YEAR FROM :I_DATETIME),
                                                   :I_QUANTITY,
                                                   (:I_QUANTITY * :LSALGSPRISSTK),
                                                   (:I_QUANTITY * :LVEJETKOSTPRISSTK),
                                                   (:I_QUANTITY * :LSALGSPRISSTK) - ((:I_QUANTITY * :LSALGSPRISSTK) / (1 + (:LMOMSSATS / 100))));

      /*Insert transaction to log stock regulation*/
      INSERT INTO TRANSAKTIONER (
          DATO,
          KUNDESALG,
          INDLAEST,
          ART,
          BETVEJ,
          LEVNAVN,
          FARVE_NAVN,
          LAENGDE_NAVN,
          STOERRELSE_NAVN,
          SALGKR,
          KOSTPR,
          VALUTAKR,
          VALUTA_TEKST,
          MOMSKR,
          DEBNR,
          RABAT,
          RABAT_MOMS,
          EKSPEDIENT,
          MKODE,
          VNRTRANS,
          VAREFRVSTRNR,
          PAKKELINJE,
          BEREGNKOSTPRIS,
          TRANSKATALOGVARE,
          BONNR,
          KASSENR,
          KATEGORI5,
          TILBUTIK,
          SERVICEYDELSE,
          ALENE_TEKST,
          TILBAGEFORELSE,
          RETUROMS,
          VAREGRPID,
          BONTEXT,
          SALGSTK,
