CREATE OR ALTER PROCEDURE P_UPDATEDEPARTMENTVARIANT (
    I_MASTER_ID TYPE OF COLUMN CREATEUPDATE_ITEM.ID,
    I_BARCODE TYPE OF COLUMN VAREFRVSTR.V509INDEX)
RETURNS (
    O_SUCCES INTEGER,
    O_MESSAGE BLOB SUB_TYPE BINARY SEGMENT SIZE 80)
AS
DECLARE VARIABLE LPRICEID TYPE OF COLUMN CREATEUPDATE_PRICES.ID;
DECLARE VARIABLE LCOSTPRICE TYPE OF COLUMN CREATEUPDATE_PRICES.COSTPRICE;
DECLARE VARIABLE LRECOMMENDEDRETAILPRICE TYPE OF COLUMN CREATEUPDATE_PRICES.RECOMMENDEDRETAILPRICE;
DECLARE VARIABLE LLASTPURCHASEPRICE TYPE OF COLUMN CREATEUPDATE_PRICES.LASTPURCHASEPRICE;
DECLARE VARIABLE LVAT_NAME TYPE OF COLUMN CREATEUPDATE_PRICES.VAT_NAME;
DECLARE VARIABLE LVAT TYPE OF COLUMN CREATEUPDATE_PRICES.VAT;
DECLARE VARIABLE LUNIT TYPE OF COLUMN CREATEUPDATE_PRICES.UNIT;
DECLARE VARIABLE LMINIMUMSTOCK TYPE OF COLUMN CREATEUPDATE_PRICES.MINIMUMSTOCK;
DECLARE VARIABLE LREORDERQUANTITY TYPE OF COLUMN CREATEUPDATE_PRICES.REORDERQUANTITY;
DECLARE VARIABLE LREORDERMETHOD TYPE OF COLUMN CREATEUPDATE_PRICES.REORDERMETHOD;
DECLARE VARIABLE LCURRENCY TYPE OF COLUMN CREATEUPDATE_PRICES.CURRENCY;
DECLARE VARIABLE LCOUNTRY TYPE OF COLUMN CREATEUPDATE_PRICES.COUNTRY;
DECLARE VARIABLE LDEPARTMENTS TYPE OF COLUMN CREATEUPDATE_PRICES.DEPARTMENTS;
DECLARE VARIABLE LUPDATEDEPARTMENT TYPE OF COLUMN AFDELING.AFDELINGSNUMMER;
DECLARE VARIABLE LUPDATECURRENCY TYPE OF COLUMN VALUTALINIER.KURS;
DECLARE VARIABLE LCURRENTKPR TYPE OF COLUMN VAREFRVSTR_DETAIL.VEJETKOSTPRISSTK;
DECLARE VARIABLE LCURRENTSPR TYPE OF COLUMN VAREFRVSTR_DETAIL.SALGSPRISSTK;
DECLARE VARIABLE LKPR TYPE OF COLUMN VAREFRVSTR_DETAIL.VEJETKOSTPRISSTK;
DECLARE VARIABLE LSPR TYPE OF COLUMN VAREFRVSTR_DETAIL.SALGSPRISSTK;
DECLARE VARIABLE LUPDATERECEIPTNUMBER INTEGER;
DECLARE VARIABLE LUPDATEDATETIME DATE;
BEGIN
  /* ************************
    Created: 10-03-2025
    Author: MIvi
    Purpose:
    This procedure will handle department specific records (variants) we need to Update
    At this point we know its about UPDATE (originated from either a create or an update) and that record exists with the UUID in CREATEUPDATE_ITEM.
    All individuel items are present in CREATEUPDATE_ITEMS.

    We need to:
     - Select records.
     - For each record update CREATEUPDATE_ITEM with information about state
     - Validate record.
     - Update item. This means:
       - Check if item is there in head item (VARER). If it does not exists. Skip it.
       - Check if item is there in variants (VAREFRVSTR). If it does not exists. Skip it.
       - If the head item exists:
         - Check for fields we need to update and update
       - If the variant exists:
         - Check for fields we need to update and update
       - This will also handle head item discount and variant discount

             We know we just updated or created  1 variant.
             We now look at the prices to this one.
             Combinations are:
             1: Nothing.
             2: By Country
             3: By currency
             4: By departments

             We will handle in the above order. This will mean:

             1: Nothing is set - all variants to every department will get updated. Cost- and retailprices will be set using system currency rate if nessesary then
             2: Departments with this Country will be updated.
             3: Departments with this currency will be updated.
             4: Departments in this list will be updated.
             Consequences if you are using all of them at the same time, we could see up to 4 updates to just 1 record.

  ************************ */


  :LCOSTPRICE = 0;
  :LRECOMMENDEDRETAILPRICE = 0;
  :LVAT_NAME = '';
  :LVAT = 0;
  :LUNIT = '';
  :LMINIMUMSTOCK = 0;
  :LREORDERQUANTITY = 0;
  :LREORDERMETHOD = 0;
  :LCURRENCY = '';
  :LCOUNTRY = '';
  :LDEPARTMENTS = '';
  :LUPDATEDEPARTMENT = '';
  :LUPDATECURRENCY = 0;
  :LCURRENTKPR = 0;
  :LCURRENTSPR = 0;
  :LKPR = 0;
  :LSPR = 0;
  :LUPDATERECEIPTNUMBER = 0;
  :LUPDATEDATETIME = 'NOW';
  :O_MESSAGE = '';


  FOR SELECT
          CREATEUPDATE_PRICES.ID,
          CREATEUPDATE_PRICES.COSTPRICE,
          CREATEUPDATE_PRICES.RECOMMENDEDRETAILPRICE,
          CREATEUPDATE_PRICES.LASTPURCHASEPRICE,
          CREATEUPDATE_PRICES.VAT_NAME,
          CREATEUPDATE_PRICES.VAT,
          CREATEUPDATE_PRICES.UNIT,
          CREATEUPDATE_PRICES.MINIMUMSTOCK,
          CREATEUPDATE_PRICES.REORDERQUANTITY,
          CREATEUPDATE_PRICES.REORDERMETHOD,
          CREATEUPDATE_PRICES.CURRENCY,
          CREATEUPDATE_PRICES.COUNTRY,
          CREATEUPDATE_PRICES.DEPARTMENTS
      FROM CREATEUPDATE_PRICES
      WHERE
          MASTER_ID = :I_MASTER_ID
      ORDER BY
          CREATEUPDATE_PRICES.DEPARTMENTS,
          CREATEUPDATE_PRICES.COUNTRY,
          CREATEUPDATE_PRICES.CURRENCY
      INTO :LPRICEID,
           :LCOSTPRICE,
           :LRECOMMENDEDRETAILPRICE,
           :LLASTPURCHASEPRICE,
           :LVAT_NAME,
           :LVAT,
           :LUNIT,
           :LMINIMUMSTOCK,
           :LREORDERQUANTITY,
           :LREORDERMETHOD,
           :LCURRENCY,
           :LCOUNTRY,
           :LDEPARTMENTS
  DO
  BEGIN
    /*Now we have whatever prices and department specific information available.
                Lets do an update*/

    IF (((:LCURRENCY IS NULL) OR
        (:LCURRENCY = '')) AND
        ((:LCOUNTRY IS NULL) OR
        (:LCOUNTRY = '')) AND
        ((:LDEPARTMENTS IS NULL) OR
        (:LDEPARTMENTS = ''))) THEN
    BEGIN
      /*No specific limit set - Update all and currency departments must be updated via calculations
        This means - Update all departments. Those with other currency than head department will get values calculated*/

      /*Lets get departments department number currency*/
      /*We will order thise via currency */
      FOR SELECT
              S1.AFDELING_ID,
              VL.KURS
          FROM STAMDATA_PRG_EXT S1
              INNER JOIN VALUTALINIER VL ON
                    VL.VALUTA_TEKST = S1.STDVALUTA
              INNER JOIN VALUTA V ON
                    (VL.VALUTA_TEKST = V.TEKST)

          ORDER BY
              VL.KURS
          INTO :LUPDATEDEPARTMENT,
               :LUPDATECURRENCY
      DO
      BEGIN
        :LKPR = (:LCOSTPRICE / :LUPDATECURRENCY * 100);
        :LSPR = (:LRECOMMENDEDRETAILPRICE / :LUPDATECURRENCY * 100);
        IF ((:LCOSTPRICE IS NULL) AND
            (:LRECOMMENDEDRETAILPRICE IS NULL)) THEN
        BEGIN
          /*Both costprice and retail price are NULL - N update ON prices*/
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  No prices set - no update needed on prices ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        IF (:LCOSTPRICE IS NULL) THEN
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  N/A  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        IF (:LRECOMMENDEDRETAILPRICE IS NULL) THEN
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  ' || :LKPR || ' ' || '  Retailprice: N/A' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  ' || :LKPR || ' ' || '  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END

        /*Sinse no dpartment, currency or country l?mit was set, we need to set calculated prices*/
        SELECT
            O_RECEIPTNUMBER
        FROM P_UPDATEITEMS_DEPARTMENTVARIANT(:I_BARCODE, :LUPDATEDEPARTMENT, :LKPR, :LLASTPURCHASEPRICE, :LSPR, :LMINIMUMSTOCK, :LREORDERQUANTITY, :LUNIT, :LREORDERMETHOD, :LUPDATEDATETIME, :LUPDATERECEIPTNUMBER)
        INTO :LUPDATERECEIPTNUMBER;

        /*Now lets handle discounts if present*/
        /*There was no limit, so we need to calculcate price and do and */
        EXECUTE PROCEDURE P_CREATEUPDATE_ITEMDISCOUNTS(:LPRICEID,
                                                       :I_BARCODE,
                                                       :LUPDATEDEPARTMENT,
                                                       :LUPDATECURRENCY);
      END
    END

    IF ((NOT(:LCOUNTRY IS NULL)) AND
        (:LCOUNTRY <> '')) THEN
    BEGIN
      /*Country set*/
      /*Lets get departments with the correpsonding country codey*/
      /*We will order thise via currency */
      FOR SELECT
              S1.AFDELING_ID,
              VL.KURS
          FROM STAMDATA_PRG_EXT S1
              INNER JOIN VALUTALINIER VL ON
                    VL.VALUTA_TEKST = S1.STDVALUTA
          WHERE
              S1.AFDELING_ID IN (SELECT
                                     AFDELING.AFDELINGSNUMMER
                                 FROM AFDELING
                                     INNER JOIN LANDE ON
                                           LANDE.LANDEKODE = AFDELING.LAND_ID
                                           AND LANDE.EU_LANDEKODE = :LCOUNTRY)
          ORDER BY
              VL.KURS
          INTO :LUPDATEDEPARTMENT,
               :LUPDATECURRENCY
      DO
      BEGIN
        :LKPR = :LCOSTPRICE;
        :LSPR = :LRECOMMENDEDRETAILPRICE;
        IF ((:LCOSTPRICE IS NULL) AND
            (:LRECOMMENDEDRETAILPRICE IS NULL)) THEN
        BEGIN
          /*Both costprice and retail price are NULL - N update ON prices*/
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  No prices set - no update needed on prices ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        IF (:LCOSTPRICE IS NULL) THEN
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  N/A  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        IF (:LRECOMMENDEDRETAILPRICE IS NULL) THEN
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  ' || :LKPR || ' ' || '  Retailprice: N/A' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  ' || :LKPR || ' ' || '  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        /*Sinse we have update for an country, prices are assumed to be in this countrys curreny. */
        SELECT
            O_RECEIPTNUMBER
        FROM P_UPDATEITEMS_DEPARTMENTVARIANT(:I_BARCODE, :LUPDATEDEPARTMENT, :LKPR, :LLASTPURCHASEPRICE, :LSPR, :LMINIMUMSTOCK, :LREORDERQUANTITY, :LUNIT, :LREORDERMETHOD, :LUPDATEDATETIME, :LUPDATERECEIPTNUMBER)
        INTO :LUPDATERECEIPTNUMBER;

        /*Now lets handle discounts if present*/
        /*There was no limit, so we need to calculcate price and do and */
        EXECUTE PROCEDURE P_CREATEUPDATE_ITEMDISCOUNTS(:LPRICEID,
                                                       :I_BARCODE,
                                                       :LUPDATEDEPARTMENT,
                                                       100);/*Assumed currency is ok*/
      END
    END

    IF ((NOT(:LCURRENCY IS NULL)) AND
        (:LCURRENCY <> '')) THEN
    BEGIN
      /*Currency set*/
      /*Lets get departments with the correpsonding country codey*/
      /*We will order thise via currency */
      FOR SELECT
              S1.AFDELING_ID,
              VL.KURS
          FROM STAMDATA_PRG_EXT S1
              INNER JOIN VALUTALINIER VL ON
                    VL.VALUTA_TEKST = S1.STDVALUTA
              INNER JOIN VALUTA V ON
                    (VL.VALUTA_TEKST = V.TEKST)
          WHERE
              V.ISOKODE = :LCURRENCY
          ORDER BY
              VL.KURS
          INTO :LUPDATEDEPARTMENT,
               :LUPDATECURRENCY
      DO
      BEGIN
        :LKPR = :LCOSTPRICE;
        :LSPR = :LRECOMMENDEDRETAILPRICE;
        IF ((:LCOSTPRICE IS NULL) AND
            (:LRECOMMENDEDRETAILPRICE IS NULL)) THEN
        BEGIN
          /*Both costprice and retail price are NULL - N update ON prices*/
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  No prices set - no update needed on prices ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        IF (:LCOSTPRICE IS NULL) THEN
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  N/A  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        IF (:LRECOMMENDEDRETAILPRICE IS NULL) THEN
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  ' || :LKPR || ' ' || '  Retailprice: N/A' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        ELSE
        BEGIN
          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  ' || :LKPR || ' ' || '  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
        END
        /*Sinse we have update for a currency, prices are assumed to be in this currency curreny. */
        SELECT
            O_RECEIPTNUMBER
        FROM P_UPDATEITEMS_DEPARTMENTVARIANT(:I_BARCODE, :LUPDATEDEPARTMENT, :LKPR, :LLASTPURCHASEPRICE, :LSPR, :LMINIMUMSTOCK, :LREORDERQUANTITY, :LUNIT, :LREORDERMETHOD, :LUPDATEDATETIME, :LUPDATERECEIPTNUMBER)
        INTO :LUPDATERECEIPTNUMBER;

        /*Now lets handle discounts if present*/
        /*There was no limit, so we need to calculcate price and do and */
        EXECUTE PROCEDURE P_CREATEUPDATE_ITEMDISCOUNTS(:LPRICEID,
                                                       :I_BARCODE,
                                                       :LUPDATEDEPARTMENT,
                                                       100);/*Assumed currency is ok*/
      END
    END

    IF ((NOT(:LDEPARTMENTS IS NULL)) AND
        (:LDEPARTMENTS <> '')) THEN
    BEGIN
      /*departments set*/
      /*Lets get departments with the correpsonding country codey*/
      /*We will order thise via currency */
      FOR SELECT
              S1.AFDELING_ID,
              VL.KURS
          FROM STAMDATA_PRG_EXT S1
              INNER JOIN VALUTALINIER VL ON
                    VL.VALUTA_TEKST = S1.STDVALUTA
          WHERE
              S1.AFDELING_ID IN (SELECT
                                     SPLIT_VALUE
                                 FROM P_SPLIT_STRING(:LDEPARTMENTS, ','))
          ORDER BY
              VL.KURS
          INTO :LUPDATEDEPARTMENT,
               :LUPDATECURRENCY
      DO
      BEGIN
        :LKPR = :LCOSTPRICE;
        :LSPR = :LRECOMMENDEDRETAILPRICE;
        IF ((:LCOSTPRICE IS NULL) AND
            (:LRECOMMENDEDRETAILPRICE IS NULL)) THEN
        BEGIN
