          :O_MESSAGE = :O_MESSAGE || '    Updating (all) department ' || COALESCE(:LUPDATEDEPARTMENT, '') || '  with currency rate ' || COALESCE(:LUPDATECURRENCY, '') || '  Costprice:  N/A  Retailprice: ' || :LSPR || ' ' || ASCII_CHAR(13) || ASCII_CHAR(10);
