CREATE OR ALTER PROCEDURE SET_FARVEOMSETLAGER (
    VNR VARCHAR(30),
    AFD VARCHAR(15),
    MDR INTEGER,
    AAR INTEGER,
    LOMSSTK NUMERIC(15,2),
    LSALGSPRIS NUMERIC(15,2),
    LKOSTPRIS NUMERIC(15,2),
    LMOMS NUMERIC(15,2))
AS
  BEGIN
    if (not exists (select * from Farve_OMSETNING where Navn=:VNr and Afdeling_ID=:Afd and Aar=:Aar and Mdr=:Mdr)) then
    begin
      insert into Farve_OMSETNING (Navn, Aar, Mdr, Afdeling_ID) Values (:VNr,:Aar,:Mdr,:Afd);
    end
    Update Farve_OMSETNING Set LAntal = :LOmsStk,
                                        LSalgspris = :LSalgspris,
                                        LKostpris = :LKostpris,
                                        LMoms = :LMoms
      Where
        Navn=:VNr and Aar=:AAr and Mdr=:Mdr and Afdeling_ID=:Afd;
  END^


