CREATE OR ALTER PROCEDURE VVKONTROLAFD (
    NYAFD VARCHAR(15),
    NUAFD VARCHAR(15))
RETURNS (
    UVNR VARCHAR(30),
    ANTOPR INTEGER,
    ANTKONTR INTEGER)
AS
DECLARE VARIABLE VSTOERRELSE_NAVN VARCHAR(30) CHARACTER SET NONE; 
DECLARE VARIABLE VLAENGDE_NAVN VARCHAR(30) CHARACTER SET NONE; 
DECLARE VARIABLE VVAREGRPID VARCHAR(30) CHARACTER SET NONE; 
DECLARE VARIABLE VLEVERID VARCHAR(30) CHARACTER SET NONE; 
DECLARE VARIABLE VOPFYLDMETODE INTEGER; 
DECLARE VARIABLE VENHEDSNOGLE INTEGER; 
DECLARE VARIABLE VSALGSPRISSTK NUMERIC(15,2); 
DECLARE VARIABLE VVEJETKOSTPRISSTK NUMERIC(15,2); 
DECLARE VARIABLE VMOMSSATS VARCHAR(25); 
DECLARE VARIABLE VVALUTA_NAVN VARCHAR(10); 
DECLARE VARIABLE VNORMALSTKANTAL NUMERIC(15,2); 
DECLARE VARIABLE VGENBESTILLING NUMERIC(15,2); 
DECLARE VARIABLE VMINBEHOLDNING NUMERIC(15,2); 
DECLARE VARIABLE VOPRETDATO DATE; 
DECLARE VARIABLE VSIDSTEBEV DATE; 
DECLARE VARIABLE VFARVE_NAVN VARCHAR(30) CHARACTER SET NONE; 
DECLARE VARIABLE VV509INDEX VARCHAR(30) CHARACTER SET NONE; 
DECLARE VARIABLE VVAREPLU_ID VARCHAR(30) CHARACTER SET NONE; 
begin 
   AntOpr   = 0; 
   AntKontr = 0; 
   For Select 
        a.VAREPLU_ID, 
        a.FARVE_NAVN, 
        a.LAENGDE_NAVN, 
        a.STOERRELSE_NAVN, 
        a.V509INDEX, 
        a.MINBEHOLDNING, 
        a.GENBESTILLING, 
        a.NORMALSTKANTAL, 
        a.VALUTA_NAVN, 
        a.MOMSSATS, 
        a.VEJETKOSTPRISSTK, 
        A.OPRETTELSESDATO, 
        A.SIDSTEBEV, 
        a.SALGSPRISSTK, 
        a.ENHEDSNOGLE, 
        a.OpfyldMetode, 
        a.LeverID, 
        a.VareGrpID 
   from varefrvstr_detail A 
   where 
     A.Afdeling_ID=:NUAFD 
   into 
        :vVAREPLU_ID, 
        :vFARVE_NAVN, 
        :vLAENGDE_NAVN, 
        :vSTOERRELSE_NAVN, 
        :vV509INDEX, 
        :vMINBEHOLDNING, 
        :vGENBESTILLING, 
        :vNORMALSTKANTAL, 
        :vVALUTA_NAVN, 
        :vMOMSSATS, 
        :vVEJETKOSTPRISSTK, 
        :vOpretDato, 
        :vSidsteBev, 
        :vSALGSPRISSTK, 
        :vENHEDSNOGLE, 
        :vOpfyldMetode, 
        :vLeverID, 
        :vVareGrpID 
   do 
   Begin 
        UVnr = :vVAREPLU_ID; 
        AntKontr = AntKontr + 1; 
        if (not (exists (select v509index from varefrvstr_detail where v509index = :vv509index and afdeling_ID = :nyAfd))) then 
        begin 
          AntOpr = AntOpr + 1; 
          Insert Into VareFrvStr_Detail ( 
                  VAREPLU_ID, 
                  FARVE_NAVN, 
                  LAENGDE_NAVN, 
                  STOERRELSE_NAVN, 
                  V509INDEX, 
                  MINBEHOLDNING, 
                  GENBESTILLING, 
                  NORMALSTKANTAL, 
                  VALUTA_NAVN, 
                  MOMSSATS, 
                  VEJETKOSTPRISSTK, 
                  OPRETTELSESDATO, 
                  SIDSTEBEV, 
                  SALGSPRISSTK, 
                  ENHEDSNOGLE, 
                  OpfyldMetode, 
                  LeverID, 
                  VareGrpID, 
                  AFDELING_ID) 
          VAlues ( 
                  :vVAREPLU_ID, 
                  :vFARVE_NAVN, 
                  :vLAENGDE_NAVN, 
                  :vSTOERRELSE_NAVN, 
                  :vV509INDEX, 
                  :vMINBEHOLDNING, 
                  :vGENBESTILLING, 
                  :vNORMALSTKANTAL, 
                  :vVALUTA_NAVN, 
                  :vMOMSSATS, 
                  :vVEJETKOSTPRISSTK, 
                  :vOpretDato, 
                  :vSidsteBev, 
                  :vSALGSPRISSTK, 
                  :vENHEDSNOGLE, 
                  :vOpfyldMetode, 
                  :vLeverID, 
                  :vVareGrpID, 
                  :nyafd); 
        end 
      suspend; 
   end 
end^




